

--Crear base de datos empresa
CREATE DATABASE empresa;

--Usar base de datos empresa;
USE empresa;

--Crear tabla departamento
CREATE TABLE departamento( cod_depto INTEGER PRIMARY KEY, nombre VARCHAR(70) NOT NULL, localidad VARCHAR(70) NOT NULL); 

--Crear tabla empleados
CREATE TABLE empleados(cod_emp INTEGER PRIMARY KEY, nombre VARCHAR(70), apellido1 VARCHAR(70), apellido2 VARCHAR(70) , contratado DATE, departamento INTEGER,responsable INTEGER,trabajo VARCHAR(70), salario INTEGER);

--Añade clave foranea
ALTER TABLE empleados ADD FOREIGN KEY (departamento) REFERENCES departamento(cod_depto);

